<div class="row">    <div class="col-md-12">        <table class="table table-responsive table-bordered">            <thead>            <tr>                <th width="150">วันที่โอน</th>                <th width="100">จำนวนเงินโอน</th>                <th width="150">หลักฐานการโอน</th>                <th>หมายเหตุ</th>            </tr>            </thead>            <tbody>            <?php if($model):?>                <?php foreach($model as $k=>$v):?>                    <?php                        $fileName = "";                        if($v->image){                            $fileName = "{$storageUrl}/uploads/{$v->image}";                        }else{                            $fileName = "{$storageUrl}/uploads/noimage.png";                        }                    ?>                    <tr>                        <td><?= isset($v->date)?\appxq\sdii\utils\SDdate::mysql2phpDateTime($v->date):''; ?></td>                        <td><?= isset($v->price)?number_format($v->price):''?></td>                        <td>                            <a target="_blank" href="<?= $fileName; ?>">                                <img src="<?= $fileName; ?>" class="img img-responsive" style="width:80px;">                            </a>                        </td>                        <td><?= isset($v->note)?$v->note:''?></td>                    </tr>                <?php endforeach;?>            <?php else: ?>            <tr>                <td colspan="4" class="text-center">ไม่พบหลักฐานการชำระเงิน</td>            </tr>            <?php endif; ?>            </tbody>        </table>    </div></div><?php \richardfan\widget\JSRegister::begin();?><script>    function previewPayment(){        let order_id = '<?= $order_id; ?>';        let url = '<?= \yii\helpers\Url::to(['/order/preview-payment'])?>';        $.get(url,{order_id:order_id},function (result) {            $("#payment-preview").html(result);        });        return false;    }</script><?php \richardfan\widget\JSRegister::end();?>