<?phpnamespace frontend\classes;use Yii;use yii\web\UploadedFile;class CNProduct{    public static function getProductById($pro_id){        $product = \backend\modules\products\models\Products::find()            ->where('id=:id AND rstat not in(0,3)',[                ':id' => $pro_id            ])->one();        return $product;    }    public static function uploadImage($model, $attribute){        $image = UploadedFile::getInstances($model,$attribute);        $fileName = "";        if ($image) {            $file = $image[0];            $realFileName = \appxq\sdii\utils\SDUtility::getMillisecTime();            $folder = "/web/uploads/";            $path = Yii::getAlias('@storage') . "{$folder}";            $type = $file->extension;            $filePath = "{$path}/{$realFileName}.{$type}";            if ($file->saveAs("{$filePath}")) {                $fileName = "{$realFileName}.{$type}";            }        }        return $fileName;    }}