<?phpnamespace frontend\controllers;use common\modules\user\classes\CNUserFunc;use yii\data\ActiveDataProvider;use yii\web\Controller;class OrderController extends Controller{    public function actionIndex()    {        $data = \backend\modules\products\models\Orders::find()->where('create_by=:user_id',[            ':user_id' => CNUserFunc::getUserId()        ])->orderBy(['create_date'=>SORT_DESC]);        $dataProvider = new ActiveDataProvider([            'query' => $data,            'pagination' => [                'pageSize' => 20,            ],        ]);        return $this->render('index', [            'dataProvider' => $dataProvider,        ]);    }    public function actionDetail(){        $order_id = \Yii::$app->request->get('order_id');        $data = \backend\modules\products\models\Orders::find()            ->where('order_id=:order_id',[            ':order_id' => $order_id        ])->one();        return $this->render('detail', [            'data'=>$data        ]);    }}