<?phpnamespace frontend\controllers;use appxq\sdii\utils\VarDumper;use backend\modules\products\models\OrderDetails;use common\modules\user\classes\CNUserFunc;use frontend\classes\CNOrder;use yii\data\ActiveDataProvider;use yii\web\Controller;class OrderController extends Controller{    public function actionIndex()    {        $data = \backend\modules\products\models\Orders::find()->where('create_by=:user_id',[            ':user_id' => CNUserFunc::getUserId()        ])->orderBy(['create_date'=>SORT_DESC]);        $dataProvider = new ActiveDataProvider([            'query' => $data,            'pagination' => [                'pageSize' => 20,            ],        ]);        return $this->render('index', [            'dataProvider' => $dataProvider,        ]);    }    public function actionDetail(){        $order_id = \Yii::$app->request->get('order_id');        $order = \backend\modules\products\models\Orders::findOne($order_id);        $detail = OrderDetails::find()            ->where('order_id=:order_id',[                ':order_id' => $order_id            ])            ->all();        //VarDumper::dump($order);        return $this->render('detail', [            'detail'=>$detail,            'order'=>$order        ]);    }}