<div class="row">    <div class="col-md-12">        <table class="table table-responsive table-bordered">            <thead>            <tr>                <th width="150">วันที่โอน</th>                <th width="50">จำนวนเงินโอน</th>                <th>หลักฐานการโอน</th>                <th>หมายเหตุ</th>            </tr>            </thead>            <tbody>            <?php if($model):?>                <?php foreach($model as $k=>$v):?>                    <?php                    $fileName = "";                    if($model->image){                        $fileName = "{$storageUrl}/uploads/{$model->image}";                    }else{                        $fileName = "{$storageUrl}/uploads/noimage.png";                    }                    ?>                    <tr>                        <td><?= isset($v->date)?\appxq\sdii\utils\SDdate::mysql2phpDateTime($v->date):''; ?></td>                        <td><?= isset($v->price)?number_format($v->price):''?></td>                        <td><img src="<?= $fileName; ?>" class="img img-responsive"></td>                        <td><?= isset($v->note)?$v->note:''?></td>                    </tr>                <?php endforeach;?>            <?php endif; ?>            </tbody>        </table>    </div></div>