<div class="row">    <div class="col-md-12">        <table class="table table-responsive table-bordered">            <thead>            <tr>                <th width="150">วันที่โอน</th>                <th width="100">จำนวนเงินโอน</th>                <th width="150">หลักฐานการโอน</th>                <th>หมายเหตุ</th>                <th width="50"></th>            </tr>            </thead>            <tbody>            <?php if($model):?>                <?php foreach($model as $k=>$v):?>                    <?php                        $fileName = "";                        if($v->image){                            $fileName = "{$storageUrl}/uploads/{$v->image}";                        }else{                            $fileName = "{$storageUrl}/uploads/noimage.png";                        }                    ?>                    <tr>                        <td><?= isset($v->date)?\appxq\sdii\utils\SDdate::mysql2phpDateTime($v->date):''; ?></td>                        <td><?= isset($v->price)?number_format($v->price):''?></td>                        <td>                            <a target="_blank" href="<?= $fileName; ?>">                                <img src="<?= $fileName; ?>" class="img img-responsive" style="width:50px;">                            </a>                        </td>                        <td><?= isset($v->note)?$v->note:''?></td>                        <td>                            <a class="btn-del" href="#" data-id="<?= $v->id; ?>">ลบ</a>                        </td>                    </tr>                <?php endforeach;?>            <?php endif;            use appxq\sdii\helpers\SDHtml;            use appxq\sdii\helpers\SDNoty; ?>            </tbody>        </table>    </div></div><?php \richardfan\widget\JSRegister::begin();?><script>    function previewPayment(){        let order_id = '<?= $order_id; ?>';        let url = '<?= \yii\helpers\Url::to(['/order/preview-payment'])?>';        $.get(url,{order_id:order_id},function (result) {            $("#payment-preview").html(result);        });        return false;    }    $(".btn-del").on('click',function () {        let id = $(this).attr('data-id');        let url = '<?= \yii\helpers\Url::to(['/order/del-payment'])?>';        bootbox.confirm({            message: "คุณแน่ใจว่าต้องการลบหรือไม่?",            buttons: {                confirm: {                    label: '<i class="fa fa-check"></i> ใช่',                    className: 'btn-success'                },                cancel: {                    label: '<i class="fa fa-times"></i> ไม่',                    className: 'btn-default'                }            },            callback: function (result) {                if(result === true){                    $.post(url,{id:id}).done(function(result) {                        if(result.status == 'success') {                            swal({                                title: result.message,                                text: result.message,                                type: result.status,                                timer: 2000                            });                            previewPayment();                        } else {                            swal({                                title: result.message,                                text: result.message,                                type: result.status,                                timer: 2000                            });                        }                    }).fail(function() {                        <?= SDNoty::show("'" . SDHtml::getMsgError() . "Server Error'", '"error"')?>                        console.log('server error');                    });                }            }        });        return false;    });</script><?php \richardfan\widget\JSRegister::end();?>